import React, { useEffect, useState } from 'react';

const Profile = () => {
  const [Student, setStudent] = useState(null);
  const [Teacher, setTeacher] = useState(null);

  useEffect(() => {
    // Fetch user profile data
    const fetchProfile = async () => {

      const response = await fetch("http://localhost:8080/authorization/getrole", {
        // headers: {
        //   'Authorization': `Bearer ${localStorage.getItem('token')}`,
        // },
      });

      if (response.ok) {
        const role = response.text();
        console.log(role);
        const url = role === "Teacher"? "http://localhost:8080/authorization/getteacher": "http://localhost:8080/authorization/getstudent";


          fetch(url)
          .then(res=>res.json())
          .then((result)=>{
            console.log('Fetched students:', result); // Dodaj t
            role === "Teacher"? setTeacher(result) : setStudent(result);
          }
        ).catch(error => console.error('Error fetching teacher:', error));
      }
    };

    fetchProfile();
  }, []);
  var user = Student? Student : Teacher;
  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>Profile</h2>
      <p>Username: {user.name}</p>
      <p>Email: {user.address}</p>
    </div>
  );
};

export default Profile;
