import React, { useEffect, useState } from 'react';
import { makeStyles } from '@mui/styles';
import TextField from '@mui/material/TextField';
import { Container ,Paper,Button} from '@mui/material';

const useStyles = makeStyles((theme) => ({
  // root: {
  //   '& > *': {
  //     margin: theme.spacing(1),
  //   },
  // },
}));

export default function Teacher() {
    const paperStyle={padding:'50px 20px', width:600,margin:"20px auto"}
    const[name,setName]=useState('')
    const[address,setAddress]=useState('')
    const[Teachers,setTeachers]=useState([])
    const classes = useStyles();

  const handleClick=(e)=>{  
    e.preventDefault()
    const Teacher={name,address}
    console.log(Teacher)
    fetch("http://localhost:8080/authorization/addTeacher",{
      method:"POST",
      headers:{"Content-Type":"application/json"},
      body:JSON.stringify(Teacher)

  }).then(()=>{
    console.log("New Teacher added")
  })

}

useEffect(()=>{
  fetch("http://localhost:8080/authorization/getAllTeachers")
  .then(res=>res.json())
  .then((result)=>{
    console.log('Fetched teachers:', result); // Dodaj t
    setTeachers(result);
  }
).catch(error => console.error('Error fetching Teachers:', error));
},[])
  return (

    <Container>
        <Paper elevation={3} style={paperStyle}>
            <h1 style={{color:"blue"}}><u>Add Teacher</u></h1>

    <form className={classes.root} noValidate autoComplete="off">

      <TextField id="outlined-basic" label="Teacher Name" variant="outlined" fullWidth 
      value={name}
      onChange={(e)=>setName(e.target.value)}
      />
      <TextField id="outlined-basic" label="Teacher Adress" variant="outlined" fullWidth
      value={address}
      onChange={(e)=>setAddress(e.target.value)}
      />
      <Button variant="contained" color="secondary" onClick={handleClick}>
  Submit
</Button>
    </form>

    </Paper>
    <h1>Teachers</h1>
{
    <Paper elevation={3} style={paperStyle}>

      {Teachers.map(Teacher=>(
        <Paper elevation={6} style={{margin:"10px",padding:"15px", textAlign:"left"}} key={Teacher.id}>
         Id:{Teacher.id}<br/>
         Name:{Teacher.name}<br/>
         Address:{Teacher.address}

        </Paper>
      ))
}


    </Paper> }



    </Container>
  );
}
