import TextField from '@mui/material/TextField';
import { Container ,Paper,Button,Box} from '@mui/material';
import {FormControl} from '@mui/material';
import React, { useEffect, useState } from 'react';

const TeacherProfile = () => {

  const paperStyle={padding:'50px 20px', width:600,margin:"20px auto"}
  const classes = useStyles();
  const [teacher, setTeacher] = useState(null);
  const [name, setName] = useState("");
  const [content, setContent] = useState("");
  const [maxPoints, setmaxPoints] = useState("");
  const [correctSolution, setcorrectSolution] = useState("");
  const [introduction, setIntroduction] = useState("");
  const [exercises, setExercises] = useState([]);
  const [isFormVisible, setIsFormVisible] = useState(false);

  const showForm=()=>{
    setIsFormVisible(true);
  };
  const closeForm=()=>{
    setIsFormVisible(false);
  };

  const deleteExercise=(e)=>{

    const url = "http://localhost:8080/exercise/delete";
    fetch(url,{
      method:"POST",
      headers:{"Content-Type":"application/json"},
      body:e.target.value

    }).then(()=>{
      window.location.reload()
    })

  }

  const addExercise=(e)=>{

    e.preventDefault()

    const exercise={name,introduction,content,maxPoints,correctSolution}

    const url = "http://localhost:8080/exercise/";
        fetch(url,{
          method:"POST",
          headers:{"Content-Type":"application/json"},
          body:JSON.stringify(exercise)

    }).then(()=>{
      window.location.reload();
      console.log("New exercise added")
    })
    closeForm();
  };
  useEffect(() => {
    const fetchTeacherProfile = async () => {
    const response = await fetch("http://localhost:8080/user/getteacher");

      if (response.ok) {
        const teacherData = await response.json();
        setTeacher(teacherData[0]); // Assuming the API returns an array with the teacher data
      }
      fetch("http://localhost:8080/user/getExercises")
      .then(res=>res.json())
      .then((result)=>{
          setExercises(result);
      })
    };

    fetchTeacherProfile();
  }, []);

  if (!teacher) {
    return <div>Loading...</div>;
  }

  return (

    <Container>
        <Paper>
        <div>
      <h2>Teacher Profile </h2>
      <p>Username: {teacher.name}</p>
      <p>Email: {teacher.email}</p>
      {/* Add more fields as necessary */}
    </div>     
       <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>

       <Box display="flex" flexDirection="column" gap={2}>
      <Button variant="contained" color="secondary" onClick={showForm}>
        Dodaj zadanie
      </Button>
      </Box>
    </div>   
    </Paper>
    {isFormVisible && (
    <Container>
        <Paper elevation={3} style={paperStyle}>

    <form className={classes.root} noValidate autoComplete="off">

      <TextField id="outlined-basic" label="Nazwa" variant="outlined" fullWidth
      value={name}
      onChange={(e)=>setName(e.target.value)}
      />

      <TextField id="outlined-basic" label="Treść" variant="outlined" fullWidth
      value={content}
      onChange={(e)=>setContent(e.target.value)}
      />

      <TextField id="outlined-basic" label="Wstęp teoretyczny" variant="outlined" fullWidth
      value={introduction}
      onChange={(e)=>setIntroduction(e.target.value)}
      />
      <TextField id="outlined-basic" label="Poprawne rozwiązanie" variant="outlined" fullWidth
      value={correctSolution}
      onChange={(e)=>setcorrectSolution(e.target.value)}
      />
       <TextField id="outlined-basic" label="max ilość punktów" variant="outlined" fullWidth
      value={maxPoints}
      onChange={(e)=>setmaxPoints(e.target.value)}
      />

    <FormControl fullWidth>
  </FormControl>
       <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>

       <Box display="flex" flexDirection="column" gap={2}>
      <Button variant="contained" color="secondary" onClick={addExercise}>
        Dodaj
      </Button>
      </Box>
    </div>
    </form>

    </Paper>

    </Container>
    )}
    <Container>
      <h1>Exercises</h1>
    {
    <Paper elevation={3} style={paperStyle}>

      {exercises.map(exercise=>(
        <Paper elevation={6} style={{margin:"10px",padding:"15px", textAlign:"left"}} key={exercise.id}>
         Content:{exercise.content}<br/>
         Points:{exercise.maxPoints}
         <button value={exercise.id} onClick={deleteExercise}>Usuń</button>

        </Paper>
      ))
    }
    </Paper>
    }
    </Container>
    </Container>

  );
};

export default TeacherProfile;
