import { makeStyles } from '@mui/styles';
import TextField from '@mui/material/TextField';
import { Container ,Paper,Button,Box} from '@mui/material';
import { FilledInput, IconButton,InputAdornment } from "@mui/material";
import { VisibilityOffOutlined, VisibilityOutlined } from "@mui/icons-material";
import {MenuItem} from '@mui/material';
import {Select,InputLabel,FormControl} from '@mui/material';
import React, { useEffect, useState } from 'react';

const TeacherProfile = () => {
  const [teacher, setTeacher] = useState(null);
  const [text, setText] = useState("");
  const [points, setPoints] = useState("");
  const [isFormVisible, setIsFormVisible] = useState(false);
  const showForm=()=>{
    setIsFormVisible(true);
  };
  const classes = useStyles();
  const addExercise=(e)=>{
    e.preventDefault()
    const exercise={text,points}
    const url = "http://localhost:8080/exercise/add";
        fetch(url,{
          method:"POST",
          headers:{"Content-Type":"application/json"},
          body:JSON.stringify(exercise)

      }).then(()=>{
        console.log("New exercise added")
      })
  };
  useEffect(() => {
    // Fetch teacher profile data
    const fetchTeacherProfile = async () => {
      const response = await fetch("http://localhost:8080/authorization/getteacher");

      if (response.ok) {
        const teacherData = await response.json();
        setTeacher(teacherData[0]); // Assuming the API returns an array with the teacher data
      }
    };

    fetchTeacherProfile();
  }, []);

  if (!teacher) {
    return <div>Loading...</div>;
  }

  return (

    <Container>
        <Paper>
        <div>
      <h2>Teacher Profile</h2>
      <p>Username: {teacher.name}</p>
      <p>Email: {teacher.address}</p>
      {/* Add more fields as necessary */}
    </div>     
       <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>

       <Box display="flex" flexDirection="column" gap={2}>
      <Button variant="contained" color="secondary" onClick={showForm}>
        Dodaj zadanie
      </Button>
      </Box>
    </div>


    </Paper>
    {isFormVisible && (
    <Container>
        <Paper elevation={3} style={paperStyle}>

    <form className={classes.root} noValidate autoComplete="off">

      <TextField id="outlined-basic" label="email" variant="outlined" fullWidth
      value={text}
      onChange={(e)=>setText(e.target.value)}
      />
       <TextField id="outlined-basic" label="max ilość punktów" variant="outlined" fullWidth
      value={points}
      onChange={(e)=>setPoints(e.target.value)}
      />

    <FormControl fullWidth>
  </FormControl>
       <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>

       <Box display="flex" flexDirection="column" gap={2}>
      <Button variant="contained" color="secondary" onClick={addExercise}>
        Zarejestruj
      </Button>
      </Box>
    </div>
    </form>

    </Paper>

    </Container>
    )}
    </Container>

  );
};

export default TeacherProfile;
