import { makeStyles } from '@mui/styles';
import TextField from '@mui/material/TextField';
import { Container ,Paper,Button,Box} from '@mui/material';
import { FilledInput, IconButton,InputAdornment } from "@mui/material";
import { VisibilityOffOutlined, VisibilityOutlined } from "@mui/icons-material";
import {MenuItem} from '@mui/material';
import {Select,InputLabel,FormControl} from '@mui/material';
import React, { useEffect, useState } from 'react';

const TeacherProfile = () => {
  const [teacher, setTeacher] = useState(null);

  useEffect(() => {
    // Fetch teacher profile data
    const fetchTeacherProfile = async () => {
      const response = await fetch("http://localhost:8080/authorization/getteacher");

      if (response.ok) {
        const teacherData = await response.json();
        setTeacher(teacherData[0]); // Assuming the API returns an array with the teacher data
      }
    };

    fetchTeacherProfile();
  }, []);

  if (!teacher) {
    return <div>Loading...</div>;
  }

  return (

    <Container>
        <Paper>
        <div>
      <h2>Teacher Profile</h2>
      <p>Username: {teacher.name}</p>
      <p>Email: {teacher.address}</p>
      {/* Add more fields as necessary */}
    </div>     
       <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>

       <Box display="flex" flexDirection="column" gap={2}>
      <Button variant="contained" color="secondary">
        Dodaj zadanie
      </Button>
      </Box>
    </div>


    </Paper>

    </Container>
  );
};

export default TeacherProfile;
