import React, {useEffect, useState } from 'react';
import { TextField, IconButton, Paper, Typography } from '@mui/material';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
import { makeStyles } from '@mui/styles';

const useStyles = makeStyles({
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    gap: '10px', // Odstęp między elementami
  },
  textFieldContainer: {
    display: 'flex',
    alignItems: 'center',
    gap: '10px', // Odstęp między polem tekstowym a przyciskiem
  },
  textField: {
    backgroundColor: '#000', // Czarny kolor tła
    color: '#fff', // Biały kolor tekstu
    '& .MuiInputBase-input': {
      color: '#fff', // Biały kolor tekstu
    },
    '& .MuiFormLabel-root': {
      color: '#fff', // Biały kolor etykiety
    },
  },
  button: {
    color: '#fff', // Biały kolor ikony
    backgroundColor: '#000', // Czarny kolor tła
    '&:hover': {
      backgroundColor: '#333', // Ciemniejszy czarny na hover
    },
  },
  output: {
    backgroundColor: '#000', // Czarny kolor tła
    color: '#fff', // Biały kolor tekstu
    padding: '10px',
    height: '200px', // Stała wysokość
    width: '200px', // Stała szerokość
    marginTop: '10px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: 'center',
  },
});

export default function Solution({task}) {

  const paperStyle={padding:'50px 20px', width:600,margin:"20px auto"}
  const classes = useStyles();
  const [inputValue, setInputValue] = useState('');
  const [output, setOutput] = useState('');


  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleButtonClick = () => {
    fetch("http://localhost:8080/exercise/python", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(inputValue)
    }).then(res => res.text())
      .then((result) => {
        console.log('Exec', result);
        setOutput(result);
      })
      .catch((error) => {
        console.error('Error:', error);
        setOutput('Error occurred');
      });
  };

  return (
    <div className={classes.container}>
      <div className={classes.textFieldContainer}>
        <TextField
          className={classes.textField}
          variant="outlined"
          placeholder="Wpisz coś..."
          value={inputValue}
          onChange={handleInputChange}
          fullWidth
        />
        <IconButton
          className={classes.button}
          onClick={handleButtonClick}
          aria-label="submit"
        >
          <ArrowForwardIcon />
        </IconButton>
      </div>
      <Paper className={classes.output}>
        <Typography variant="body1">
          {output}
        </Typography>
      </Paper>
      </div>
  )};
