import React, {useEffect, useState } from 'react';
import { makeStyles } from '@mui/styles';
import { useNavigate } from 'react-router-dom';
import {Paper,Button,Box} from '@mui/material';

const useStyles = makeStyles({
  container: {
    display: 'flex',
    flexDirection: 'column',  
    alignItems: 'center',
    gap: '10px', // Odstęp między elementami
  },
  textFieldContainer: {
    display: 'flex',
    alignItems: 'center',
    gap: '10px', // Odstęp między polem tekstowym a przyciskiem
  },
  textField: {
    backgroundColor: '#000', // Czarny kolor tła
    color: '#fff', // Biały kolor tekstu
    '& .MuiInputBase-input': {
      color: '#fff', // Biały kolor tekstu
    },
    '& .MuiFormLabel-root': {
      color: '#fff', // Biały kolor etykiety
    },
  },
  button: {
    color: '#fff', // Biały kolor ikony
    backgroundColor: '#000', // Czarny kolor tła
    '&:hover': {
      backgroundColor: '#333', // Ciemniejszy czarny na hover
    },
  },
  output: {
    backgroundColor: '#000', // Czarny kolor tła
    color: '#fff', // Biały kolor tekstu
    padding: '10px',
    height: '200px', // Stała wysokość
    width: '200px', // Stała szerokość
    marginTop: '10px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: 'center',
  },
});

export default function Exercise() {
  const paperStyle={padding:'50px 20px', width:600,margin:"20px auto"}
  const classes = useStyles();
  const[exercises,setExercises]=useState([]);
  const navigate = useNavigate();

  const openSolution=(e)=>{
    navigate('/solution/:'+e.target.value);
  }


useEffect(()=>{
  fetch("http://localhost:8080/exercise/")
  .then(res=>res.json())
  .then((result)=>{
    console.log('Fetched students:', result); // Dodaj t
    setExercises(result);
  }
).catch(error => console.error('Error fetching students:', error));
},[])

  return (
    <div className={classes.container}>
      <h1>Zadania</h1>
    {
    <Paper elevation={3} style={paperStyle}>

      {exercises.map(exercise=>(
        <Paper elevation={6} style={{margin:"10px",padding:"15px", textAlign:"left"}} key={exercise.id}>
        <h3> {exercise.name}</h3><br/>
        {exercise.maxPoints}
    <div>
       <Box display="flex" flexDirection="column" gap={2}>
      <Button variant="contained" color="secondary" onClick={openSolution}>
        Wykonaj
      </Button>
      </Box>
    </div>
        </Paper>
      ))
    }
    </Paper>
    }
    </div>


  );
}
