import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import * as yup from 'yup';
import axios from 'axios';
import { useTracked } from './path-to-useTracked'; // Assuming you have a custom useTracked hook
import { setItem } from './path-to-utils'; // Assuming you have a utility function to set items in localStorage
import { authApiEndpoints } from './path-to-api-endpoints'; // Assuming you have an API endpoints configuration
import { Messages } from 'primereact/messages';
import { Card } from 'primereact/card';
import LocaleToggle from './LocaleToggle'; // Assuming LocaleToggle is a custom component

const Register = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('student'); // default to student

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Implement registration logic
    const response = await fetch('/api/register', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, email, password, role }),
    });

    if (response.ok) {
      // Handle successful registration
      console.log('Registered successfully');
    } else {
      // Handle error
      console.log('Registration failed');
    }
  };


  return (
    <Card className="p-sm-12 p-md-6 p-lg-4" style={{ borderRadius: 5, minHeight: 65 }}>
    <div>
      <h2>Register</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Username</label>
          <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
        </div>
        <div>
          <label>Email</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </div>
        <div>
          <label>Password</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>
        <div>
          <label>Role</label>
          <select value={role} onChange={(e) => setRole(e.target.value)}>
            <option value="student">Student</option>
            <option value="teacher">Teacher</option>
          </select>
        </div>
        <button type="submit">Register</button>
      </form>
    </div>
    </Card>
  );
};

export default Register;
