import { makeStyles } from '@mui/styles';
import TextField from '@mui/material/TextField';
import { Container ,Paper,Button,Box} from '@mui/material';
import {FormControl} from '@mui/material';
import React, { useEffect, useState } from 'react';
const useStyles = makeStyles((theme) => ({

}));
const TeacherProfile = () => {
  const paperStyle={padding:'50px 20px', width:600,margin:"20px auto"}
  const [teacher, setTeacher] = useState(null);
  const [content, setContent] = useState("");
  const [maxPoints, setmaxPoints] = useState("");
  const [expectedOutput, setexpectedOutput] = useState("");
  const [exercises, setExercises] = useState([]);

  const [isFormVisible, setIsFormVisible] = useState(false);
  const showForm=()=>{
    setIsFormVisible(true);
  };

  function deleteExcTeacher(e){
      console.log(e);

      const url = "http://localhost:8080/authorization/deleteExc";
      fetch(url,{
        method:"POST",
        headers:{"Content-Type":"application/json"},
        body:e

    }).then(()=>{

     // window.location.reload(); // Reload the page
      console.log("Usunieto")
    })

  }

  const deleteExercise=(e)=>{
    deleteExcTeacher(e.target.value);
    const url = "http://localhost:8080/exercise/delete";
    fetch(url,{
      method:"POST",
      headers:{"Content-Type":"application/json"},
      body:JSON.stringify(e.target.value)

  }).then(()=>{

   // window.location.reload(); // Reload the page
    console.log("Usunieto")
  })

  }
  const classes = useStyles();
  const addExercise=(e)=>{
    e.preventDefault()

    const exercise={content,maxPoints,expectedOutput}
    const url = "http://localhost:8080/exercise/add";
        fetch(url,{
          method:"POST",
          headers:{"Content-Type":"application/json"},
          body:JSON.stringify(exercise)

      }).then(()=>{
        console.log("New exercise added")
      })
    const urlv2 = "http://localhost:8080/authorization/addExercise";
      fetch(urlv2,{
        method:"POST",
        headers:{"Content-Type":"application/json"},
        body:JSON.stringify(exercise)

    }).then(()=>{
      console.log("New exercise added")
    })
  };
  useEffect(() => {
    // Fetch teacher profile data
    const fetchTeacherProfile = async () => {
      const response = await fetch("http://localhost:8080/authorization/getteacher");

      if (response.ok) {
        const teacherData = await response.json();
        setTeacher(teacherData[0]); // Assuming the API returns an array with the teacher data
      }
      fetch("http://localhost:8080/authorization/getExercises")
    .then(res=>res.json())
    .then((result)=>{
      console.log('Fetchediiii:', result); // Dodaj t
      setExercises(result);
    })
    };

    fetchTeacherProfile();
  }, []);

  if (!teacher) {
    return <div>Loading...</div>;
  }

  return (

    <Container>
        <Paper>
        <div>
      <h2>Teacher Profile</h2>
      <p>Username: {teacher.name}</p>
      <p>Email: {teacher.address}</p>
      {/* Add more fields as necessary */}
    </div>     
       <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>

       <Box display="flex" flexDirection="column" gap={2}>
      <Button variant="contained" color="secondary" onClick={showForm}>
        Dodaj zadanie
      </Button>
      </Box>
    </div>   
    </Paper>
    {isFormVisible && (
    <Container>
        <Paper elevation={3} style={paperStyle}>

    <form className={classes.root} noValidate autoComplete="off">

      <TextField id="outlined-basic" label="email" variant="outlined" fullWidth
      value={content}
      onChange={(e)=>setContent(e.target.value)}
      />

      <TextField id="outlined-basic" label="expected output" variant="outlined" fullWidth
      value={expectedOutput}
      onChange={(e)=>setexpectedOutput(e.target.value)}
      />
       <TextField id="outlined-basic" label="max ilość punktów" variant="outlined" fullWidth
      value={maxPoints}
      onChange={(e)=>setmaxPoints(e.target.value)}
      />

    <FormControl fullWidth>
  </FormControl>
       <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>

       <Box display="flex" flexDirection="column" gap={2}>
      <Button variant="contained" color="secondary" onClick={addExercise}>
        Zarejestruj
      </Button>
      </Box>
    </div>
    </form>

    </Paper>

    </Container>
    )}
    <Container>
      <h1>Exercises</h1>
    {
    <Paper elevation={3} style={paperStyle}>

      {exercises.map(exercise=>(
        <Paper elevation={6} style={{margin:"10px",padding:"15px", textAlign:"left"}} key={exercise.id}>
         Content:{exercise.content}<br/>
         Points:{exercise.maxPoints}
         <button value={JSON.stringify(exercise)} onClick={deleteExercise}>Usuń</button>
        </Paper>
      ))
    }
    </Paper>
    }
    </Container>
    </Container>

  );
};

export default TeacherProfile;
