import React, { useEffect, useState } from 'react';

const TeacherProfile = () => {
  const [teacher, setTeacher] = useState(null);

  useEffect(() => {
    // Fetch teacher profile data
    const fetchTeacherProfile = async () => {
      const response = await fetch("http://localhost:8080/authorization/getteacher");

      if (response.ok) {
        const teacherData = await response.json();
        setTeacher(teacherData[0]); // Assuming the API returns an array with the teacher data
      }
    };

    fetchTeacherProfile();
  }, []);

  if (!teacher) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>Teacher Profile</h2>
      <p>Username: {teacher.name}</p>
      <p>Email: {teacher.address}</p>
      {/* Add more fields as necessary */}
    </div>
  );
};

export default TeacherProfile;
